mapper.select:
		select `flowing_id`,`flowing_no`,`assets_befrom`,`assets_after`,`assets_change`,`order_no`,`type`,`create_time`,`user_id` from t_front_flowing_record_vfs

		  		   and flowing_id = #{flowingId} 
		  		   and flowing_no = #{flowingNo} 
		  		   and assets_befrom = #{assetsBefrom} 
		  		   and assets_after = #{assetsAfter} 
		  		   and assets_change = #{assetsChange} 
		  		   and order_no = #{orderNo} 
		  		   and type = #{type} 
		  		   and user_id = #{userId} 

				and	create_time between #{startTime} and #{endTime}




                order by ${sort} ${order}


                order by flowing_id desc



			limit #{offset}, #{limit}

select count(*) from t_front_flowing_record_vfs

  		   and flowing_id = #{flowingId} 
  		   and flowing_no = #{flowingNo} 
  		   and assets_befrom = #{assetsBefrom} 
  		   and assets_after = #{assetsAfter} 
  		   and assets_change = #{assetsChange} 
  		   and order_no = #{orderNo} 
  		   and type = #{type} 
  		   and create_time = #{createTime} 
  		   and user_id = #{userId}
